mapper.select:
SELECT id, password
FROM user
WHERE id=#{id} and password = #{password}

SELECT id, nickname, picture_url, created_at, updated_at
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET refreshToken = #{refreshToken}
WHERE id = #{id}

mapper.select:
SELECT refreshToken
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET refreshToken = #{refreshToken, jdbcType=VARCHAR}
WHERE id = #{id}
