mapper.update:
UPDATE user
SET password=#{password}, nickname=#{nickname}, picture_url=#{pictureUrl,  jdbcType=VARCHAR}
WHERE user_id=#{userId}

mapper.select:
SELECT user_id
FROM user
WHERE user_id=#{userId} and password=#{password}

mapper.insert:
UPDATE user
SET refresh_token=#{token}
WHERE user_id=#{userId}

mapper.select:
SELECT refresh_token
FROM user
WHERE user_id=#{userId}
